import React, { useState } from "react";
import PhoneInput, {
  formatPhoneNumber,
  isValidPhoneNumber,
} from "react-phone-number-input";
import "react-phone-number-input/style.css"; // Import CSS for default styling

const App = () => {
  const [phone, setPhone] = useState("");
  const [country, setCountry] = useState("");

  const handlePhoneChange = (value) => {
    setPhone(value);
  };

  const handleCountryChange = (event) => {
    setCountry(event.target.value);
    setPhone(""); // Reset phone number when country changes
  };

  const validatePhoneNumber = () => {
    if (!isValidPhoneNumber(phone)) {
      return "Invalid phone number";
    }

    if (country && !isValidPhoneNumber(phone, country)) {
      return `Invalid phone number for ${country}`;
    }

    return null;
  };

  return (
    <div>
      <label>
        Select Country:
        <select value={country} onChange={handleCountryChange}>
          <option value="">Select Country</option>
          <option value="US">United States</option>
          <option value="GB">United Kingdom</option>
          {/* Add more countries as needed */}
        </select>
      </label>
      <br />
      <label>
        Phone Number:
        <PhoneInput
          country={country}
          value={phone}
          onChange={handlePhoneChange}
          placeholder="Enter phone number"
        />
      </label>
      {validatePhoneNumber() && (
        <p style={{ color: "red" }}>{validatePhoneNumber()}</p>
      )}
    </div>
  );
};

export default App;
