import React, { useState } from "react";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css"; // Import CSS for default styling
import { isValidPhoneNumber, parsePhoneNumber } from "react-phone-number-input";

function App() {
  const [phone, setPhone] = useState("");
  const [isValid, setIsValid] = useState(true);

  const handlePhoneChange = (value) => {
    setPhone(value);
    setIsValid(isValidPhoneNumber(value) && isMobilePhoneNumber(value));
  };

  const isMobilePhoneNumber = (phoneNumber) => {
    const parsedPhoneNumber = parsePhoneNumber(phoneNumber);
    return parsedPhoneNumber ? parsedPhoneNumber.getType() === "MOBILE" : false;
  };

  return (
    <div>
      <label>
        Phone Number:
        <PhoneInput
          value={phone}
          onChange={handlePhoneChange}
          placeholder="Enter phone number"
        />
      </label>
      {!isValid && <p style={{ color: "red" }}>Invalid mobile phone number</p>}
    </div>
  );
}

export default App;
