import React, { useState } from "react";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css"; // Import CSS for default styling
import { parsePhoneNumber } from "react-phone-number-input";

function App() {
  const [phone, setPhone] = useState("");
  const [isValid, setIsValid] = useState(true);

  const handlePhoneChange = (value) => {
    if (typeof value === "string") {
      // Ensure value is a string before parsing
      setPhone(value);
      setIsValid(validatePhoneNumber(value));
    }
  };

  const validatePhoneNumber = (phoneNumber) => {
    const parsedPhoneNumber = parsePhoneNumberFromString(phoneNumber, "ZZ");
    if (!parsedPhoneNumber) return false; // Invalid phone number format
    // Check if it's a mobile number
    if (parsedPhoneNumber.getType() !== "MOBILE") return false;
    return parsedPhoneNumber.isValid();
  };
  return (
    <div>
      <label>
        Phone Number:
        <PhoneInput
          value={phone}
          onChange={handlePhoneChange}
          placeholder="Enter phone number"
        />
      </label>
      {!isValid && <p style={{ color: "red" }}>Invalid phone number</p>}
    </div>
  );
}

export default App;
